<?phpclass cleanup {				var $db;	var $nwObj;		function cleanup(&$db=NULL,$mode='daily')	{		if (is_null($db)) { 			require_once(PATH_CORE.'/classes/db.class.php');			$this->db=new cloudDatabase();		} else			$this->db=$db;		switch ($mode) {			case 'daily':								// delete old newswire stories				require_once('newswire.class.php');					$this->nwObj=new newswire($this->db);				$this->nwObj->cleanup();				require_once('content.class.php');					$this->cObj=new content($this->db);				$this->cObj->cleanup();			break;			case 'weekly':			break;			case 'monthly':			break;			default:			break;		}	}		function flushDatabase() {		$this->db->delete("Comments");		$this->db->delete("Content");		$this->db->delete("cronJobs");		$this->db->delete("Folders");		$this->db->delete("FolderLinks");		$this->db->delete("Log");		$this->db->delete("Newswire");		$this->db->delete("SystemStatus");		$this->db->delete("User");					}}	?>